mapper.select:
select
  id, user_id, user_name, message_type, content, create_time
from message_record


        and id = #{id}


        and user_id = #{userId}


        and user_name = #{userName}


        and message_type = #{messageType}


        and content = #{content}


        and create_time = #{createTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from message_record


        and id = #{id}


        and user_id = #{userId}


        and user_name = #{userName}


        and message_type = #{messageType}


        and content = #{content}


        and create_time = #{createTime}
